import { StyleSheet, Text, SafeAreaView, View, Platform, Image, TextInput } from "react-native"
import { useState } from "react"
import colors from "../assets/colors"

const professors = [
  {
    name: "Tanzir Ahmed",
    school: "Texas A&M University",
    department: "CSCE",
    gpa: "3.1"
  },
  {
    name: "Teresa Leyk",
    school: "Texas A&M University",
    department: "CSCE",
    gpa: "3.1"
  },
  {
    name: "Dylan Shell",
    school: "Texas A&M University",
    department: "CSCE",
    gpa: "3.1"
  },
]

export function Home() {

  const [search, setSearch] = useState("search by professor")
  const [searchBG, setSearchBG] = useState(colors.PURPLE)
  const [filteredData, setFilteredData] = useState([]);
  const [wordEntered, setWordEntered] = useState("");

  const handleSearch = (text:string) => {
    const searchWord = text;
    setWordEntered(searchWord);
    console.log(searchWord)
    const newFilter:any = professors.filter((value) => {
      console.log(value.name.toLowerCase().includes(searchWord.toLowerCase()))
      return value.name.toLowerCase().includes(searchWord.toLowerCase());
    });

    if (searchWord === "") {
      setFilteredData([]);
    } else {
      setFilteredData(newFilter);
    }
  }
  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.nav}>
        <Text style={styles.title}>
          Profesi 
        </ Text>
        <Text style={styles.username}>
          John Doe 
        </ Text>
        <Image 
          source={{ uri: 'https://picsum.photos/50/50' }}
          style={styles.userImage}
        />
      </View>
      <View style={{width: "80%"}}>
        <TextInput 
            onChangeText={handleSearch}
            onFocus={() => setSearchBG(colors.GREEN)}
            onBlur={() => setSearchBG(colors.PURPLE)}
            value={wordEntered}
            //clearTextOnFocus={true}
            placeholder="search by professor"
            //defaultValue="search by professor"
            style={[styles.inputStyles, {borderColor: searchBG}]}
          />
      </View>
      {filteredData.length != 0 && (
        <View style={{width: "90%", height: "auto", flexDirection: "column"}}>
          {filteredData.slice(0, 15).map((value:{name:string, school:string, department:string, gpa:string}, key) => {
            return (
            <View style={styles.resultContainer}>
              <Text style={{padding: 5}}> 
                {value.name}
              </Text>
              <Text style={{padding: 5}}> 
                {value.school}
              </Text>
              <Text style={{padding: 5}}> 
                {value.department}
              </Text>
              <Text style={{padding: 5, textAlign: "right"}}> 
                {value.gpa}
              </Text>
            </View>
            );
          })} 
        </View>
      )}
    </ SafeAreaView>
  )
}

const styles = StyleSheet.create({
  container:{
    justifyContent: "flex-start",
    alignItems: "center",
    flex: 1,
    flexDirection: "column",
  },
  title:{
    fontSize: 30,
    textAlign: "left",
    flex: 3
  },
  nav:{
    width: "100%",
    flexDirection: "row",
    justifyContent: "space-around",
    alignItems: "center",
    paddingHorizontal: 20,
    marginTop: 20,
    marginBottom: 40
  },
  username:{
    flex: 3,
    textAlign: "right",
    paddingRight: 10,
    fontSize: 20
  },
  userImage:{
    width: 50,
    height: 50,
    borderWidth: 1,
    borderRadius: 50 
  },
  inputStyles:{
    borderWidth: 2,
    borderRadius: 5,
    padding: 5,
    paddingLeft: 5,
    fontSize: 15,
  },
  resultContainer:{
    borderRadius: 25,
    backgroundColor: colors.RED,
    width: "50%",
    height: "auto",
    flexDirection: "column",
    padding: 5,
    margin: 10,
  }
})
