//IMPORTS
import {
  Text,
  SafeAreaView,
  View,
  TextInput,
  TouchableOpacity,
  Pressable,
  ScrollView,
} from 'react-native'
import AsyncStorage from '@react-native-async-storage/async-storage'
import { colors } from '../assets/colors'
import { NativeStackNavigationProp } from '@react-navigation/native-stack'
import { RootStackParamList } from '../RootStackParams'
import { useNavigation } from '@react-navigation/native'
import { Icon } from 'react-native-elements'
import { React, useEffect, useState } from 'react'

type coursesScreenProp = NativeStackNavigationProp<
  RootStackParamList,
  'Courses'
>

interface Props {
  route: {
    params: {
      courseName: string
    }
  }
}

// FZF STRING MATCH
// handleSearch - Params(text:string)
const handleSearch = (
  text: string,
  profList: string[],
  setProfList: any,
  originalProfList: string[]
) => {
  const searchWord = text
  setProfList(searchWord)
  const newFilter: string[] = profList.filter(
    (value: { name: string; gpa: string }) => {
      return value.name.toLowerCase().includes(searchWord.toLowerCase())
    }
  )
  if (searchWord === undefined) {
    setProfList(originalProfList)
  } else {
    setProfList(newFilter)
  }
}

async function getProfsByCourse(
  course: string,
  setProfList: any,
  setOriginalProfList: any
): Promise<any> {
  // return await fetch(`https://profesy.herokuapp.com/?name=${name}`)
  return await fetch(`http://localhost:8080/profsByCourse?course=${course}`)
    .then((result) => result.json())
    .then((result) => {
      console.log(result.courses)
      setProfList(result.courses)
      setOriginalProfList(result.courses) //save prof list for if search is empty
      return result
    })
    .catch((err) => {
      console.error(err)
    })
}
// getItem - Params(key: string, setStateItem: function) => string
const getItem = async (key: string, setStateItem: any) => {
  try {
    const val = await AsyncStorage.getItem(key)
    setStateItem(val)
    return val
  } catch (e: any) {
    console.log('error', e.message)
  }
}

export const Courses = (Props: Props) => {
  const [profList, setProfList] = useState([])
  const [originalProfList, setOriginalProfList] = useState([])
  const [searchBG, setSearchBG] = useState('black')
  const [wordEntered, setWordEntered] = useState('')

  useEffect(
    () => getProfsByCourse(Props.route.params.courseName, setProfList),
    [Props.route.params.courseName]
  )

  console.log(profList)
  return (
    <SafeAreaView
      style={{
        backgroundColor: 'black',
        height: '88%',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
      }}
    >
      <View
        style={{
          width: '90%',
        }}
      >
        <Text
          style={{
            textAlign: 'left',
            color: 'white',
            fontSize: 30,
            fontWeight: 'bold',
          }}
        >
          {Props.route.params.courseName}
        </Text>
      </View>
      {/*search bar*/}
      <View
        style={{
          width: '95%',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
        }}
      >
        <View
          style={{
            flexDirection: 'row',
            alignItems: 'center',
            justifyContent: 'center',
            backgroundColor: 'white',
            borderRadius: 5,
            width: '100%',
          }}
        >
          <Icon
            name="search"
            style={{ opacity: 0.7, marginLeft: 5 }}
            tvParallaxProperties={null}
          />
          <TextInput
            // queries both at first time for better UX
            onChangeText={(data) => {
              data.length === 0
                ? setProfList(originalProfList)
                : handleSearch(data, profList, setProfList, originalProfList)
              setWordEntered(data === undefined ? '' : data)
            }}
            value={wordEntered}
            placeholder="search"
            style={{
              borderWidth: 2,
              borderRightWidth: 0,
              borderLeftWidth: 0,
              padding: 10,
              paddingLeft: 30,
              fontSize: 15,
              borderColor: searchBG,
              width: '100%',
              marginLeft: -30,
            }}
          />
        </View>
        <View style={{ width: '100%' }}>
          {profList.length !== 0 &&
            profList.sort().map((prof: { name: string; gpa: string }) => {
              return (
                <View
                  key={undefined}
                  style={{
                    width: '100%',
                    backgroundColor: 'black',
                    borderRadius: 15,
                    flexDirection: 'row',
                    padding: 5,
                    marginVertical: 5,
                    justifyContent: 'space-between',
                  }}
                >
                  <Text
                    style={{
                      color: 'white',
                      textAlign: 'left',
                      padding: 5,
                      color: 'white',
                      fontSize: 25,
                      textAlign: 'left',
                      fontWeight: '500',
                      width: '50%',
                    }}
                  >
                    {prof.name}
                  </Text>
                  <Text
                    style={{
                      color: 'white',
                      textAlign: 'right',
                      width: '100%',
                      padding: 5,
                      paddingLeft: 15,
                      color: colors.BLUE,
                      fontSize: 25,
                      textAlign: 'right',
                      fontWeight: '800',
                      width: '50%',
                    }}
                  >
                    {prof.gpa}
                  </Text>
                </View>
              )
            })}
        </View>
      </View>
    </SafeAreaView>
  )
}
