//IMPORTS
import {
  View,
  SafeAreaView,
  Keyboard,
  StyleSheet,
  Text,
  TextInput,
  TouchableOpacity,
  ScrollView,
} from 'react-native'
import React from 'react'
import { Icon } from 'react-native-elements'
import { colors } from '../assets/colors'
import { RootStackParamList, Course as CourseType } from '../RootStackParams'
import { useState, useEffect } from 'react'
import { LineChart } from 'react-native-svg-charts'
import { G, Text as SVGText, Circle } from 'react-native-svg'
import * as shape from 'd3-shape'

interface Props {
  route: {
    params: {
      course: string
      prof: string
    }
  }
}

// Seasons object for sorting
const seasons: {
  SPRING: number
  SUMMER: number
  FALL: number
  WINTER: number
} = {
  SPRING: 3,
  SUMMER: 2,
  FALL: 1,
  WINTER: 0,
}

async function getSemesters(
  course: string,
  prof: string,
  setSemesters: Function
): Promise<any> {
  return await fetch(
    `http://localhost:8080/courseAndProf?course=${course}&prof=${prof}`
  )
    .then((result) => result.json())
    .then((result) => {
      result.message.courses !== undefined &&
        setSemesters(sortFilterCourses(course, result.message.courses))
      return result
    })
    .catch((err) => {
      console.log(err)
    })
}

function sortFilterCourses(courseName: string, courses: CourseType[]) {
  return courses
    .sort((a, b) => {
      const aY = parseInt(
        a.semester.substring(a.semester.length - 4, a.semester.length)
      )
      const bY = parseInt(
        b.semester.substring(b.semester.length - 4, b.semester.length)
      )
      const aS = a.semester.substring(0, a.semester.length - 5)
      const bS = b.semester.substring(0, b.semester.length - 5)
      return aY !== bY ? aY - bY : seasons[bS] - seasons[aS]
    })
    .filter((c: CourseType) => {
      return c.course === courseName
    })
}

export function Course(Props: Props) {
  // const semesterInfo = Props.route.params.courses
  const [semesterInfo, setSemesterInfo] = useState([])
  const semesterGPAs = semesterInfo.map((s) => {
    return parseFloat(s.semGPA)
  })
  const courseAvg =
    semesterGPAs.reduce((total, next) => total + next, 0) / semesterGPAs.length

  // SET STATES
  const [filteredData, setFilteredData] = useState<string[]>([])
  const [wordEntered, setWordEntered] = useState<any>('')
  const [searchBG, setSearchBG] = useState(colors.PURPLE)
  const [currentSemester, setCurrentSemester] = useState<any>(semesterInfo[0])
  const [graphData, setGraphData] = useState<number[]>(
    currentSemester !== undefined
      ? [
          parseInt(currentSemester.A),
          parseInt(currentSemester.B),
          parseInt(currentSemester.C),
          parseInt(currentSemester.F),
          parseInt(currentSemester.Q),
        ]
      : []
  )
  const [togglePercentages, setTogglePercentages] = useState(false)
  const [selectedNode, setSelectedNode] = useState(0)
  useEffect((): Promise<any> => {
    getSemesters(
      Props.route.params.course,
      Props.route.params.prof,
      setSemesterInfo
    )
    setCurrentSemester(semesterInfo[0])
  }, [Props.route.params.course, Props.route.params.prof, semesterInfo])

  // FZF STRING MATCH
  // handleSearch - Params(text:string)
  const handleSearch = (text: string) => {
    const searchWord = text
    setWordEntered(searchWord)
    const newFilter: any = semesterInfo.filter((value: CourseType) => {
      return value.semester.toLowerCase().includes(searchWord.toLowerCase())
    })
    if (searchWord === undefined) {
      setFilteredData(semesterInfo.map((info: CourseType) => info.semester))
    } else {
      setFilteredData(newFilter)
    }
  }

  // CHART POINTS
  function Decorator({
    x,
    y,
    data,
  }: { x: any; y: any; data: number[] } | any): any {
    return data.map((value: number, index: any) => (
      <Circle
        key={index}
        cx={x(index)}
        cy={y(value)}
        r={8.5}
        stroke={'rgb(134, 65, 244)'}
        strokeWidth={3}
        fill={selectedNode === index ? 'rgb(134, 65, 244)' : 'black'}
        onPress={() => {
          setCurrentSemester(
            semesterInfo.find((s: CourseType) => parseFloat(s.semGPA) === value)
          )
          setSelectedNode(index)
        }}
      />
    ))
  }

  return (
    <SafeAreaView style={styles.container}>
      {/*HEADER*/}
      <View style={styles.courseTitle}>
        <Text
          style={{
            fontSize: 40,
            paddingHorizontal: 30,
            paddingVertical: 10,
            color: 'white',
            fontWeight: '500',
          }}
        >
          {Props.route.params.course.substring(0, 4)}
          <Text style={{ fontWeight: '300' }}>
            {Props.route.params.course.substring(4, 7)}
          </Text>
          <Text style={{ fontSize: 20, opacity: 0.75, paddingBottom: 2 }}>
            {' '}
            {Props.route.params.prof}
          </Text>
        </Text>
      </View>

      {currentSemester !== undefined ? (
        <View
          style={{
            width: '100%',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          <View style={{ marginTop: 10, width: '70%', alignItems: 'center' }}>
            {/*SEARCH BAR*/}
            <View
              style={{
                flexDirection: 'row',
                width: '100%',
                alignItems: 'center',
                justifyContent: 'center',
              }}
            >
              <Icon
                name="search"
                style={{ opacity: 0.7, color: 'black' }}
                tvParallaxProperties={null}
              />
              <TextInput
                onChangeText={handleSearch}
                onFocus={() => {
                  setSearchBG(colors.GREEN)
                  handleSearch('')
                }}
                onBlur={() => setSearchBG(colors.PURPLE)}
                value={wordEntered}
                placeholder="search by semester"
                style={[
                  styles.inputStyles,
                  { borderColor: searchBG, paddingLeft: 30 },
                ]}
              />
            </View>

            {/*SEMSTER LIST*/}
            {filteredData.length != 0 && (
              <ScrollView
                style={{
                  width: '105%',
                  paddingHorizontal: 0,
                  flexDirection: 'column',
                  height: '40%',
                  position: 'absolute',
                  top: '16%',
                  zIndex: 3,
                  borderRadius: 10,
                }}
              >
                {filteredData.slice(0, 15).map((value: any, key) => {
                  return (
                    <TouchableOpacity
                      style={styles.resultContainer}
                      onPress={() => {
                        setCurrentSemester(value)
                        setWordEntered(value.semester)
                        setFilteredData([])
                        setGraphData([
                          parseInt(currentSemester.A),
                          parseInt(currentSemester.B),
                          parseInt(currentSemester.C),
                          parseInt(currentSemester.F),
                          parseInt(currentSemester.Q),
                        ])
                        Keyboard.dismiss()
                      }}
                      key={undefined}
                    >
                      <Text style={styles.result}>{value.semester}</Text>
                    </TouchableOpacity>
                  )
                })}
              </ScrollView>
            )}

            {/*SELECTED SEMSTER*/}
            <Text
              style={{ fontSize: 25, textAlign: 'center', marginVertical: 15 }}
            >
              <Text
                style={{
                  textAlign: 'center',
                  fontSize: 20,
                  opacity: 0.6,
                  letterSpacing: -1,
                }}
              >
                {' '}
                {currentSemester.semester.length > 0
                  ? currentSemester.semester
                  : 'N/A'}
              </Text>
            </Text>

            {/*GPA*/}
            <View
              style={{
                width: '60%',
                padding: 10,
                borderRadius: 10,
                shadowColor: colors.GRAY,
                shadowOpacity: 1,
                shadowOffset: { width: 1, height: 2 },
                shadowRadius: 1,
                backgroundColor:
                  parseFloat(currentSemester.semGPA).toFixed(2) >= 3.5
                    ? colors.BLUE
                    : parseFloat(currentSemester.semGPA).toFixed(2) >= 3.0
                    ? colors.GREEN
                    : parseFloat(currentSemester.semGPA).toFixed(2) >= 2.5
                    ? colors.ORANGE
                    : colors.RED,
              }}
            >
              <Text
                style={{
                  fontSize: 30,
                  textAlign: 'center',
                  color: 'white',
                  fontWeight: '700',
                }}
              >
                <Text style={{ fontWeight: '400', opacity: 0.8 }}>GPA </Text>
                {parseFloat(currentSemester.semGPA).toFixed(2)}
              </Text>
            </View>

            {/*GRADE DISTRIBUTION*/}
            <View
              style={{
                flexDirection: 'column',
                alignItems: 'center',
                marginTop: 15,
                paddingHorizontal: 12,
                width: '120%',
                paddingBottom: 15,
                borderBottomWidth: 0.3,
                borderBottomColor: colors.GREY,
              }}
            >
              <Text style={{ fontSize: 30, fontWeight: '300' }}>
                Grade Distribution
              </Text>

              <View
                style={{
                  flexDirection: 'row',
                  justifyContent: 'flex-start',
                  marginTop: 15,
                  alignItems: 'center',
                }}
              >
                {currentSemester.semester !== 'none' &&
                  ['A', 'B', 'C', 'F', 'Q'].map((letter) => {
                    let letterPercentage = parseFloat(
                      (currentSemester[letter] / currentSemester.CourseTotal) *
                        (100).toFixed(0)
                    )

                    return (
                      <TouchableOpacity
                        style={[
                          styles.distLetter,
                          styles[`dist${letter}`],
                          {
                            flex:
                              letterPercentage < 5
                                ? 0.06
                                : letterPercentage / 100,
                          },
                        ]}
                        onPress={() => setTogglePercentages(!togglePercentages)}
                        key={undefined}
                      >
                        <Text
                          style={[
                            { marginLeft: -16, fontWeight: '700' },
                            togglePercentages && { fontWeight: '400' },
                          ]}
                        >
                          {togglePercentages
                            ? `${letterPercentage.toFixed(0)}%`
                            : letter}
                        </Text>
                      </TouchableOpacity>
                    )
                  })}
              </View>
            </View>
          </View>

          {/*COURSE AVERAGE*/}
          <View
            style={{
              flexDirection: 'row',
              alignItems: 'center',
              justifyContent: 'center',
              width: '60%',
              marginTop: 15,
              padding: 10,
              borderRadius: 10,
              shadowColor: colors.GRAY,
              shadowOpacity: 1,
              shadowOffset: { width: 1, height: 2 },
              shadowRadius: 1,
              backgroundColor:
                parseFloat(courseAvg.toFixed(2)) >= 3.5
                  ? colors.BLUE
                  : parseFloat(courseAvg.toFixed(2)) >= 3.0
                  ? colors.GREEN
                  : parseFloat(courseAvg.toFixed(2)) >= 2.5
                  ? colors.ORANGE
                  : colors.RED,
            }}
          >
            <Text
              style={{
                fontSize: 20,
                color: 'white',
                marginRight: 10,
                opacity: 0.8,
              }}
            >
              Course Average
            </Text>
            <Text
              style={{
                fontSize: 25,
                textAlign: 'center',
                color: 'white',
                fontWeight: '500',
              }}
            >
              {courseAvg.toFixed(2)}
            </Text>
          </View>

          {/*DISTRIBUTION GRAPH*/}
          <LineChart
            data={semesterGPAs}
            style={{ height: 200, width: '90%', zIndex: 1 }}
            svg={{
              strokeWidth: 3,
              stroke:
                parseFloat(courseAvg).toFixed(2) >= 3.5
                  ? colors.BLUE
                  : parseFloat(courseAvg).toFixed(2) >= 3.0
                  ? colors.GREEN
                  : parseFloat(courseAvg).toFixed(2) >= 2.5
                  ? colors.ORANGE
                  : colors.RED,
            }}
            contentInset={{ top: 30, bottom: 20, left: 25, right: 25 }}
            curve={shape.curveCatmullRom}
          >
            <Decorator />
          </LineChart>

          {/*SIDE-SCROLL BUTTONS*/}
          <TouchableOpacity
            style={{
              zIndex: -1,
              position: 'absolute',
              bottom: 0,
              right: 0,
              height: '100%',
              width: '50%',
            }}
            onPress={() => {
              setSelectedNode(
                selectedNode === semesterInfo.length - 1 ? 0 : selectedNode + 1
              )
              setCurrentSemester(
                selectedNode + 1 === semesterInfo.length
                  ? semesterInfo[0]
                  : semesterInfo[selectedNode + 1]
              )
            }}
          />
          <TouchableOpacity
            style={{
              zIndex: -1,
              // backgroundColor: 'red',
              position: 'absolute',
              bottom: 0,
              left: 0,
              height: '100%',
              width: '50%',
            }}
            onPress={() => {
              setSelectedNode(
                selectedNode === 0 ? semesterInfo.length - 1 : selectedNode - 1
              )
              setCurrentSemester(
                selectedNode - 1 === -1
                  ? semesterInfo[semesterInfo.length - 1]
                  : semesterInfo[selectedNode - 1]
              )
            }}
          />

          <View
            style={{ width: '80%', height: 40, borderBottomWidth: 0.3 }}
          ></View>
        </View>
      ) : (
        <View
          style={{
            width: '100%',
            height: '20%',
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center',
            alignItems: 'center',
          }}
        >
          <Text style={{ color: colors.GREY, fontSize: 20 }}>Loading ...</Text>
        </View>
      )}
    </SafeAreaView>
  )
}

