import { StyleSheet, Text, SafeAreaView, View, Platform, Image, TextInput } from "react-native"
import { useState } from "react"

export const Home: React.FC<Props> = ({}) => {

  const [search, setSearch] = useState("search by professor")
  const [searchBG, setSearchBG] = useState("rgba(150, 150, 150, .5)")
  return (
    <SafeAreaView style={styles.titleContainer}>
      <View style={styles.nav}>
        <Text style={styles.title}>
          Profesi 
        </ Text>
        <Text style={styles.username}>
          John Doe 
        </ Text>
        <Image 
          source={{ uri: 'https://picsum.photos/50/50' }}
          style={styles.userImage}
        />
      </View>
      <View style={{width: "80%"}}>
        <TextInput 
            onChangeText={setSearch}
            onFocus={() => setSearchBG("#10b981")}
            onBlur={() => setSearchBG("rgba(150, 150, 150, .5)")}
            value={search}
            clearTextOnFocus={true}
            defaultValue="search by professor"
            style={[styles.inputStyles, {borderColor: searchBG}]}
          />
      </View>
    </ SafeAreaView>
  )
}

const styles = StyleSheet.create({
  titleContainer:{
    justifyContent: "center",
    alignItems: "center",
    flex: 1,
    flexDirection: "column"
    },
  title:{
    fontSize: 30,
    textAlign: "left",
    flex: 3
  },
  nav:{
    position: "absolute",
    top: 0,
    left: 0,
    width: "100%",
    height: "10%",
    marginTop: Platform.OS === "ios" ? 50: 0,
    flex: 1,
    flexDirection: "row",
    justifyContent: "space-evenly",
    alignItems: "center",
    paddingHorizontal: 20 
  },
  username:{
    flex: 2,
    textAlign: "center",
    fontSize: 20
  },
  userImage:{
    width: 50,
    height: 50,
    borderWidth: 1,
    borderRadius: 50 /2 
  },
  inputStyles:{
    borderWidth: 2,
    borderRadius: 5,
    padding: 5,
    paddingLeft: 5,
    fontSize: 15,
  }
})
