//IMPORTS
import {
  Text,
  SafeAreaView,
  View,
  TextInput,
  TouchableOpacity,
  Pressable,
  ScrollView,
} from 'react-native'
import AsyncStorage from '@react-native-async-storage/async-storage'
import { colors } from '../assets/colors'
import { NativeStackNavigationProp } from '@react-navigation/native-stack'
import { RootStackParamList } from '../RootStackParams'
import { useNavigation } from '@react-navigation/native'
import { Icon } from 'react-native-elements'
import { React, useEffect, useState } from 'react'

type coursesScreenProp = NativeStackNavigationProp<
  RootStackParamList,
  'Courses'
>

interface Props {
  route: {
    params: {
      courseName: string
    }
  }
}

// FZF STRING MATCH
// handleSearch - Params(text:string)
const handleSearch = (
  text: string,
  profList: string[],
  setProfList: any,
  originalProfList: string[]
) => {
  const searchWord = text
  setProfList(searchWord)
  const newFilter: string[] = profList.filter((value) => {
    return value.toLowerCase().includes(searchWord.toLowerCase())
  })
  if (searchWord === undefined) {
    setProfList(originalProfList)
  } else {
    setProfList(newFilter)
  }
}

async function getProfsByCourse(
  course: string,
  setProfList: any,
  setOriginalProfList: any
): Promise<any> {
  // return await fetch(`https://profesy.herokuapp.com/?name=${name}`)
  return await fetch(`http://localhost:8080/profsByCourse?course=${course}`)
    .then((result) => result.json())
    .then((result) => {
      setProfList(result.courses[0].profList)
      setOriginalProfList(result.courses[0].profList) //save prof list for if search is empty
      return result
    })
    .catch((err) => {
      console.error(err)
    })
}
// getItem - Params(key: string, setStateItem: function) => string
const getItem = async (key: string, setStateItem: any) => {
  try {
    const val = await AsyncStorage.getItem(key)
    setStateItem(val)
    return val
  } catch (e: any) {
    console.log('error', e.message)
  }
}

export const Courses = (Props: Props) => {
  const [profList, setProfList] = useState([])
  const [originalProfList, setOriginalProfList] = useState([])
  useEffect(
    () => getProfsByCourse(Props.route.params.courseName, setProfList),
    [Props.route.params.courseName]
  )

  // console.log(profList)
  return (
    <SafeAreaView>
      {/*search bar*/}
      <View
        style={{
          width: '100%',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
        }}
      >
        <View
          style={{
            flexDirection: 'row',
            alignItems: 'center',
            justifyContent: 'center',
            backgroundColor: 'white',
            borderRadius: 5,
            width: '100%',
          }}
        >
          <Icon
            name="search"
            style={{ opacity: 0.7, marginLeft: 5 }}
            tvParallaxProperties={null}
          />
          <TextInput
            // queries both at first time for better UX
            onChangeText={(data) => {
              setWordEntered(data === undefined ? '' : data)
              // if (filterType === 'p') {
              getProfessor(data, setFilteredProfData)
              // } else if
              getCourses(data, setFilteredCourseData)
              // }
            }}
            // onFocus={() => setSearchBG(colors.GREY)}
            // onBlur={() => setSearchBG(colors.GREY)}
            value={wordEntered}
            placeholder="search"
            style={[
              styles.inputStyles,
              { borderColor: searchBG, width: '100%', marginLeft: -30 },
            ]}
          />
        </View>
        <View>
          {profList.length !== 0 &&
            profList.sort().map((value) => {
              return (
                <View key={undefined}>
                  <Text>{value}</Text>
                </View>
              )
            })}
        </View>
      </View>
    </SafeAreaView>
  )
}
