//IMPORTS
import {
  Text,
  SafeAreaView,
  View,
  TextInput,
  TouchableOpacity,
  Pressable,
  ScrollView,
} from 'react-native'
import AsyncStorage from '@react-native-async-storage/async-storage'
import { colors } from '../assets/colors'
import { NativeStackNavigationProp } from '@react-navigation/native-stack'
import { RootStackParamList } from '../RootStackParams'
import { useNavigation } from '@react-navigation/native'
import { Icon } from 'react-native-elements'
import { React, useEffect, useState } from 'react'

type coursesScreenProp = NativeStackNavigationProp<
  RootStackParamList,
  'Courses'
>

async function getProfsByCourse(course: string, setData: any): Promise<any> {
  // return await fetch(`https://profesy.herokuapp.com/?name=${name}`)
  return await fetch(`http://localhost:8080/profsByCourse?course=${course}`)
    .then((result) => result.json())
    .then((result) => {
      console.log(result.courses[0].profList)
      // setData(result.courses.profList)
      setData([])
      // setData(result.message)
      return result
    })
    .catch((err) => {
      console.error(err)
    })
}
// getItem - Params(key: string, setStateItem: function) => string
const getItem = async (key: string, setStateItem: any) => {
  try {
    const val = await AsyncStorage.getItem(key)
    setStateItem(val)
    return val
  } catch (e: any) {
    console.log('error', e.message)
  }
}

export const Courses = (Props: Props) => {
  const [profList, setProfList] = useState([])
  console.log(Props.route.params.courseName)
  useEffect(getProfsByCourse(Props.route.params.courseName, setProfList), [])

  return (
    <SafeAreaView>
      {profList.map((value) => {
        ;<Pressable>
          <Text>{value}</Text>
        </Pressable>
      })}
    </SafeAreaView>
  )
}
