// IMPORTS
import {
  TouchableOpacity,
  TextInput,
  View,
  ScrollView,
  StyleSheet,
  SafeAreaView,
  Text,
} from 'react-native'
import { colors, randomColor } from '../assets/colors'
import { RootStackParamList, Course } from '../RootStackParams'
import { NativeStackNavigationProp } from '@react-navigation/native-stack'
import { useNavigation } from '@react-navigation/native'
import { useState } from 'react'
import fuzzysort from 'fuzzysort'
import React from 'react'

interface Props {
  route: { params: { profName: string; courses: Course[] } }
}

type professorScreenProp = NativeStackNavigationProp<
  RootStackParamList,
  'Professor'
>

export function Professor(Props: Props) {
  // sort all of professor courses
  const allCourses = Array.from([
    ...new Set(
      Props.route.params.courses.map((obj) => {
        return obj.course
      })
    ),
  ]).sort()

  // SET STATES
  const [wordEntered, setWordEntered] = useState('')
  const [courses, setCourses] = useState(allCourses)

  const navigation = useNavigation<professorScreenProp>()

  // FZF String match
  // handleSearch - Params(search:string, course:string[], setCourses:function)
  function handleSearch(search: string, courses: string[], setCourses: any) {
    setCourses(
      search.length === 0
        ? allCourses
        : fuzzysort.go(search, courses).map((item) => {
            return item.target
          })
    )
  }

  return (
    <SafeAreaView style={styles.container}>
      <View>
        {/*HEADERS*/}
        <View
          style={{
            display: 'flex',
            flexDirection: 'row',
            alignItems: 'flex-end',
          }}
        >
          <Text style={[styles.title]}>{Props.route.params.profName}</Text>
          <View
            style={{
              borderColor: colors.GRAY,
            }}
          >
            <Text style={{ color: 'white' }}>Courses</Text>
          </View>
        </View>

        {/*SEARCH*/}
        <View style={{ width: '95%' }}>
          <View
            style={{
              flexDirection: 'row',
              width: '100%',
              alignItems: 'center',
            }}
          >
            <TextInput
              onChangeText={(word) => {
                setWordEntered(word)
                handleSearch(word, courses, setCourses)
              }}
              value={wordEntered}
              clearTextOnFocus={true}
              placeholder="search for course"
              style={[styles.inputStyles, { borderColor: 'white' }]}
            />
          </View>
        </View>

        {/*COURSE LIST*/}
        <ScrollView style={styles.departments}>
          {courses.map((course) => {
            return (
              <TouchableOpacity
                style={[
                  styles.departmentContainer,
                  { shadowColor: randomColor() },
                ]}
                onPress={() => {
                  navigation.navigate('Course', {
                    course: course,
                    prof: Props.route.params.profName,
                  })
                }}
                key={undefined}
              >
                <Text style={styles.department}>
                  {course.substring(0, 4)}
                  <Text
                    style={{
                      color: 'white',
                      opacity: 0.8,
                      fontWeight: '300',
                      /*(parseFloat(value.overallGPA) > 3.4) ? colors.BLUE
                : (parseFloat(value.overallGPA) > 2.8) ? colors.GREEN 
                : (parseFloat(value.overallGPA)> 2.0) ? colors.ORANGE 
                : colors.RED*/
                    }}
                  >
                    {course.substring(4, course.length)}
                  </Text>
                </Text>
              </TouchableOpacity>
            )
          })}
        </ScrollView>
      </View>
    </SafeAreaView>
  )
}

// STYLES
const styles = StyleSheet.create({
  container: {
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    marginHorizontal: 'auto',
    height: '85%',
    backgroundColor: 'black',
  },
  title: {
    textAlign: 'left',
    fontSize: 40,
    color: 'white',
    margin: 0,
  },
  departmentTitle: {
    fontSize: 30,
    textAlign: 'center',
    color: 'white',
  },
  departmentContainer: {
    // marginHorizontal: 30,
    // marginVertical: 10,
    // shadowOffset: { width: 3, height: 2 },
    // shadowOpacity: 1,
    // shadowRadius: 0,
  },
  department: {
    fontSize: 35,
    textAlign: 'left',
    color: 'white',
    fontWeight: '500',
    letterSpacing: 5,
    paddingVertical: 15,
  },
  departments: {
    // flex: 6,
    // justifyContent: 'flex-start',
    // paddingBottom: 100,
    // height: '80%',
  },
  inputStyles: {
    borderWidth: 2,
    borderRadius: 5,
    padding: 5,
    paddingHorizontal: 10,
    fontSize: 15,
    width: '95%',
    color: 'white',
  },
})
