//IMPORTS
import {
  Text,
  SafeAreaView,
  View,
  TextInput,
  TouchableOpacity,
  Pressable,
  ScrollView,
} from 'react-native'
import AsyncStorage from '@react-native-async-storage/async-storage'
import { colors } from '../assets/colors'
import { NativeStackNavigationProp } from '@react-navigation/native-stack'
import { RootStackParamList } from '../RootStackParams'
import { useNavigation } from '@react-navigation/native'
import { Icon } from 'react-native-elements'
import { React, useEffect, useState } from 'react'

type coursesScreenProp = NativeStackNavigationProp<
  RootStackParamList,
  'Courses'
>

interface Props {
  route: {
    params: {
      courseName: string
    }
  }
}

// FZF STRING MATCH
// handleSearch - Params(text:string)
const handleSearch = (text: string, setProfList: any) => {
  const searchWord = text
  setProfList(searchWord)
  const newFilter: any = semesterInfo.filter((value) => {
    return value.semester.toLowerCase().includes(searchWord.toLowerCase())
  })
  if (searchWord === undefined) {
    setFilteredData(semesterInfo.map((info) => info.semester))
  } else {
    setFilteredData(newFilter)
  }
}

async function getProfsByCourse(course: string, setData: any): Promise<any> {
  // return await fetch(`https://profesy.herokuapp.com/?name=${name}`)
  return await fetch(`http://localhost:8080/profsByCourse?course=${course}`)
    .then((result) => result.json())
    .then((result) => {
      setData(result.courses[0].profList)
      return result
    })
    .catch((err) => {
      console.error(err)
    })
}
// getItem - Params(key: string, setStateItem: function) => string
const getItem = async (key: string, setStateItem: any) => {
  try {
    const val = await AsyncStorage.getItem(key)
    setStateItem(val)
    return val
  } catch (e: any) {
    console.log('error', e.message)
  }
}

export const Courses = (Props: Props) => {
  const [profList, setProfList] = useState([])
  useEffect(
    () => getProfsByCourse(Props.route.params.courseName, setProfList),
    [Props.route.params.courseName]
  )

  // console.log(profList)
  return (
    <SafeAreaView>
      <View>
        {profList.length !== 0 &&
          profList.sort().map((value) => {
            return (
              <View key={undefined}>
                <Text>{value}</Text>
              </View>
            )
          })}
      </View>
    </SafeAreaView>
  )
}
